import {Component, Inject} from '@angular/core';
import {MAT_DIALOG_DATA, MatDialogRef} from "@angular/material/dialog";

@Component({
  selector: 'app-dialog',
  templateUrl: './message.component.html',
  styleUrls: ['./message.component.css']
})
export class MessageComponent {

  lines?: [];

  constructor(public dialogRef: MatDialogRef<MessageComponent>, @Inject(MAT_DIALOG_DATA) public data: {heading:string,message:any}) {

    // this.lines = this.data.message.split('<br>').filter((line: string) => line !== '');
    console.log(this.data.message.gpa);
  }

  ngOnInit(): void { this.dialogRef.addPanelClass('custom-dialog'); }

  onNoClick(): void { this.dialogRef.close();}

}
<mat-card>
  <mat-card-title>{{data.heading}}</mat-card-title>
  <mat-card-content>
    <ng-container>
      <span>Your Year 1 GPA : </span><span class="result">{{data.message.gpa}}</span>
      <br>
      <h3>Progression Condition</h3>
      <br>
      <span>A Minimum GPA of 1.50 ?: </span> <span class="result">{{data.message.condition1}}</span><br/>
      <span>A minimum of 20 credits of C grade for GPA courses?: </span> <span class="result">{{data.message.condition2}}</span><br/>
      <span>A “PASS” grade for all EN courses? : </span> <span class="result">{{data.message.condition3}}</span><br/>
      <span>Has obtained a grade below D grade?: </span> <span class="result">{{data.message.condition4}}</span><br/>
      <br>
      <span>Year Progression : </span> <span class="result">{{data.message.prmsg}}</span><br/>

    </ng-container>
<!--    <span>{{data.message}}</span>-->
  </mat-card-content>
  <mat-dialog-actions>
    <button mat-raised-button [mat-dialog-close]="true"> OK</button>
  </mat-dialog-actions>
</mat-card>
